import axios from 'axios';
import * as Constants from './Constants';
import * as Utilities from './Utilities';
import { Network } from '@capacitor/network';

const APIMethods = {
    GET: "GET",
    POST: "POST",
    PUT: "PUT",
    DELETE: "DELETE"
}

export const APIResult = {
    SUCCESS: "true",
    FAILURE: "false"
}

export interface PurchaseSummary{
    bulk_purchase_date:string;
    total_bulk_quantity:string;
    average_bulk_fat:string;
    average_bulk_snf:string;
}

export const getWeatherReport = async (longtitude: Number, latitude: Number) => {
    let data: { [k: string]: any } = {};
    let api_id = Constants.Configs.api_id;
    return executeAPI('weather?lat='+latitude+'&lon='+longtitude+'&appid='+api_id, APIMethods.GET, false, data);
}

const executeAPI = async (endpoint: string, method: any, authRequired?: boolean, data?: any, params?: any) => {
    const status = await Network.getStatus();

    if(status.connected && status.connectionType==="wifi"){
        let headers: { [k: string]: any } = {};
        try {
            const response = await axios({
                method: method,
                url: Constants.Configs.BASE_URL + endpoint,
                data: data,
                headers: headers,
                params: params
            });
            console.log(response.data);
            return response.data;
        } catch (error) {
            return { success: false, message: 'Error Occurred in the Request!' }
        }
    } else {
        alert("No Internet Connection");
    }

}
