import { Redirect, Route } from 'react-router-dom';
import { IonApp, IonRouterOutlet, useIonViewDidEnter } from '@ionic/react';
import { IonReactRouter } from '@ionic/react-router';
import Home from './pages/Home';

/* Core CSS required for Ionic components to work properly */
import '@ionic/react/css/core.css';

/* Basic CSS for apps built with Ionic */
import '@ionic/react/css/normalize.css';
import '@ionic/react/css/structure.css';
import '@ionic/react/css/typography.css';

/* Optional CSS utils that can be commented out */
import '@ionic/react/css/padding.css';
import '@ionic/react/css/float-elements.css';
import '@ionic/react/css/text-alignment.css';
import '@ionic/react/css/text-transformation.css';
import '@ionic/react/css/flex-utils.css';
import '@ionic/react/css/display.css';

/* Theme variables */
import './theme/variables.css';
import { useEffect, useState } from 'react';
import {Network} from '@capacitor/network';
import * as Constants from './common/Constants';
import * as Utilities from './common/Utilities';
import { Geolocation } from '@capacitor/geolocation';

const App: React.FC = () => {
  const [showLoading, setShowLoading] = useState(false);
  const [latitude, setLatitude] = useState(0);
  const [longitude, setLongitude] = useState(0);

  useIonViewDidEnter(()=>{
    const coordinates = Geolocation.getCurrentPosition();
    coordinates.then(data=>{
      setLatitude(data.coords.latitude);
      setLongitude(data.coords.longitude);
    });
  });

  useEffect(()=>{
    Utilities.getPersistWeatherData().then((data)=>{
      console.log("yes");
    })
    if(Utilities.getPersistWeatherData()){

    } else {
      var interval = setInterval(getWeatherData, 7200000);
    }

  },[longitude]);

  async function getWeatherData() {
    setShowLoading(true);
    const status = await Network.getStatus();

    if(status.connected && status.connectionType==="wifi"){
      let api_id = Constants.Configs.api_id;
      const result = await fetch(Constants.Configs.BASE_URL+'weather?lat='+latitude+'&lon='+longitude+'&appid='+api_id);
      const data = await result.json();
      Utilities.persistWeatherData(data);
    }
    setShowLoading(false);
  }


  return (
    <IonApp>
      <IonReactRouter>
        <IonRouterOutlet>
          <Route exact path="/home">
            <Home />
          </Route>
          <Route exact path="/">
            <Redirect to="/home" />
          </Route>
        </IonRouterOutlet>
      </IonReactRouter>
    </IonApp>
  );
}
export default App;
