import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, useIonViewDidEnter } from '@ionic/react';
import ExploreContainer from '../components/ExploreContainer';
import './Home.css';
import { Geolocation } from '@capacitor/geolocation';
import { useEffect, useState } from 'react';
import * as ServerAPI from '../common/ServerAPI';
import Preloader from '../components/Preloader';
import AppToast from '../components/AppToast';
import * as Utilities from '../common/Utilities';
import * as Constants from '../common/Constants';

const Home: React.FC = () => {
  const [latitude, setLatitude] = useState(0);
  const [longitude, setLongitude] = useState(0);
  const [showLoading, setShowLoading] = useState(false);
  const [toastMessage, setToastMessage] = useState("");
  const [showToast, setShowToast] = useState(false);

  useIonViewDidEnter(()=>{
    const coordinates = Geolocation.getCurrentPosition();
    coordinates.then(data=>{
      setLatitude(data.coords.latitude);
      setLongitude(data.coords.longitude);
    });
  });

  useEffect(()=>{
    setShowLoading(true);

     ServerAPI.getWeatherReport(longitude,latitude).then((data)=>{
       setShowLoading(false);
       if(data) {
         console.log(data);
       }
     });
  },[longitude])


  return (
    <IonPage>
      <IonContent fullscreen>
        <div className="osahan-success vh-100 bg-weather">
          <div className="p-5 text-center">
              <i className="icofont-check-circled display-1 text-warning"></i>
              <h1 className="text-white font-weight-bold">Perundurai</h1>
              <p className="text-white font-weight-light">Today's Weather</p>
          </div>
          <div className="text-center">
            <p className="text-white weather-text">30</p>
          </div>
        </div>
      </IonContent>
    </IonPage>
  );
};

export default Home;
